import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Send, History, FolderOpen, Settings, Zap, TestTube, Folder, Cog } from 'lucide-react';
import { useSettings } from '../context/SettingsContext';
import { getTranslation } from '../locales/translations';
import './Header.css';

const Header = () => {
  const location = useLocation();
  const { language } = useSettings();

  // 根据当前页面选择不同的logo
  const getLogoIcon = () => {
    if (location.pathname === '/') {
      return <Zap className="logo-icon" />;
    } else if (location.pathname === '/history') {
      return <TestTube className="logo-icon" />;
    } else if (location.pathname === '/collections') {
      return <Folder className="logo-icon" />;
    } else if (location.pathname === '/settings') {
      return <Cog className="logo-icon" />;
    } else {
      return <Send className="logo-icon" />;
    }
  };

  return (
    <header className="header">
      <div className="header-content">
        <div className="logo">
          {getLogoIcon()}
          <h1>{getTranslation(language, 'appTitle')}</h1>
        </div>

        <nav className="nav">
          <Link 
            to="/" 
            className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
          >
            <Zap size={18} />
            {getTranslation(language, 'requestBuilder')}
          </Link>
          <Link 
            to="/history" 
            className={`nav-link ${location.pathname === '/history' ? 'active' : ''}`}
          >
            <TestTube size={18} />
            {getTranslation(language, 'history')}
          </Link>
          <Link 
            to="/collections" 
            className={`nav-link ${location.pathname === '/collections' ? 'active' : ''}`}
          >
            <Folder size={18} />
            {getTranslation(language, 'collections')}
          </Link>
          <Link 
            to="/settings" 
            className={`nav-link ${location.pathname === '/settings' ? 'active' : ''}`}
          >
            <Cog size={18} />
            {getTranslation(language, 'settings')}
          </Link>
        </nav>
      </div>
    </header>
  );
};

export default Header;
