import React, { useState, useEffect } from 'react';
import MethodSelector from './MethodSelector';
import UrlInput from './UrlInput';
import HeadersTab from './HeadersTab';
import ParamsTab from './ParamsTab';
import BodyTab from './BodyTab';
import ResponseViewer from './ResponseViewer';
import { useSettings } from '../context/SettingsContext';
import { getTranslation } from '../locales/translations';

import { Send, Clock, Save } from 'lucide-react';
import './RequestBuilder.css';

const RequestBuilder = () => {
  console.log('RequestBuilder component rendered'); // 调试日志
  const { language } = useSettings();

  const [request, setRequest] = useState({
    method: 'GET',
    url: '',
    headers: [],
    params: [],
    body: '',
    bodyType: 'json'
  });

  const [response, setResponse] = useState(null);
  const [loading, setLoading] = useState(false);
  const [activeTab, setActiveTab] = useState('headers');

  // 检查是否有重试数据需要加载
  useEffect(() => {
    const retryData = localStorage.getItem('retryRequestData');
    if (retryData) {
      try {
        const parsedData = JSON.parse(retryData);
        setRequest(parsedData);
        // 清除重试数据，避免重复加载
        localStorage.removeItem('retryRequestData');
      } catch (error) {
        console.error('解析重试数据失败:', error);
        localStorage.removeItem('retryRequestData');
      }
    }
  }, []);

  const handleSendRequest = async () => {
    console.log('Send request clicked'); // 调试日志
    if (!request.url) {
      alert(getTranslation(language, 'url'));
      return;
    }

    setLoading(true);
    setResponse(null);
    const startTime = Date.now();

    try {
      // 构建请求配置
      const config = {
        method: request.method,
        url: request.url,
        headers: {},
        timeout: 30000
      };

      // 添加请求头
      request.headers.forEach(header => {
        if (header.key && header.value) {
          config.headers[header.key] = header.value;
        }
      });

      // 添加URL参数
      if (request.params.length > 0) {
        const url = new URL(request.url);
        request.params.forEach(param => {
          if (param.key && param.value) {
            url.searchParams.append(param.key, param.value);
          }
        });
        config.url = url.toString();
      }

      // 添加请求体
      if (['POST', 'PUT', 'PATCH'].includes(request.method) && request.body) {
        if (request.bodyType === 'json') {
          config.headers['Content-Type'] = 'application/json';
          config.data = request.body;
        } else {
          config.headers['Content-Type'] = 'application/x-www-form-urlencoded';
          config.data = request.body;
        }
      }

      // 发送请求
      const response = await fetch(config.url, {
        method: config.method,
        headers: config.headers,
        body: config.data
      });

      const responseData = await response.text();
      const endTime = Date.now();

      setResponse({
        status: response.status,
        statusText: response.statusText,
        headers: Object.fromEntries(response.headers.entries()),
        data: responseData,
        time: endTime - startTime,
        size: responseData.length
      });

      // 自动保存到历史记录
      const history = JSON.parse(localStorage.getItem('apiHistory') || '[]');
      const historyItem = {
        id: Date.now(),
        timestamp: new Date().toISOString(),
        ...request,
        response: response
      };

      history.unshift(historyItem);
      if (history.length > 100) {
        history.pop();
      }

      localStorage.setItem('apiHistory', JSON.stringify(history));

    } catch (error) {
      console.error('Request failed:', error);
    } finally {
      setLoading(false);
    }
  };

  const updateRequest = (field, value) => {
    setRequest(prev => ({
      ...prev,
      [field]: value
    }));
  };

  const handleSaveToCollection = () => {
    if (!request.url) {
      alert(language === 'zh' ? '请先输入URL' : 'Please enter URL first');
      return;
    }

    // 获取当前collections
    const collections = JSON.parse(localStorage.getItem('apiCollections') || '[]');

    // 生成API名称（从URL中提取）
    let apiName = '';
    try {
      const url = new URL(request.url);
      apiName = url.pathname.split('/').filter(Boolean).pop() || 'API';
    } catch {
      apiName = 'API';
    }

    // 创建新的API项
    const newApi = {
      id: Date.now(),
      name: apiName,
      method: request.method,
      url: request.url,
      headers: request.headers,
      params: request.params,
      body: request.body,
      bodyType: request.bodyType,
      createdAt: new Date().toISOString()
    };

    // 添加到默认collection或创建新的
    if (collections.length === 0) {
      collections.push({
        id: Date.now(),
        name: language === 'zh' ? '默认集合' : 'Default Collection',
        apis: [newApi]
      });
    } else {
      // 添加到第一个collection
      collections[0].apis.push(newApi);
    }

    // 保存到localStorage
    localStorage.setItem('apiCollections', JSON.stringify(collections));

    // 显示成功消息
    alert(language === 'zh' ? 'API已收藏到集合中' : 'API added to favorites');
  };

  return (
    <div className="request-builder">
      <div className="request-header">
        <div className="request-controls">
          <MethodSelector 
            method={request.method} 
            onChange={(method) => updateRequest('method', method)} 
          />
          <UrlInput 
            url={request.url} 
            onChange={(url) => updateRequest('url', url)}
          />
          <button 
            className="btn btn-primary send-btn"
            onClick={handleSendRequest}
            disabled={loading}
            style={{ minWidth: '120px', padding: '10px 20px' }}
          >
            {loading ? <Clock size={16} /> : <Send size={16} />}
            {loading ? getTranslation(language, 'loading') : getTranslation(language, 'send')}
          </button>
          <button 
            className="btn btn-secondary save-btn"
            onClick={handleSaveToCollection}
            style={{ minWidth: '120px', padding: '10px 20px' }}
          >
            <Save size={16} />
            {language === 'zh' ? '收藏' : 'Favorite'}
          </button>
        </div>
      </div>

      <div className="main-content">
        <div className="request-section">
          <div className="request-tabs">
            <div className="tab-container">
              <button 
                className={`tab ${activeTab === 'headers' ? 'active' : ''}`}
                onClick={() => setActiveTab('headers')}
              >
                {getTranslation(language, 'headers')}
              </button>
              <button 
                className={`tab ${activeTab === 'params' ? 'active' : ''}`}
                onClick={() => setActiveTab('params')}
              >
                {getTranslation(language, 'params')}
              </button>
              <button 
                className={`tab ${activeTab === 'body' ? 'active' : ''}`}
                onClick={() => setActiveTab('body')}
              >
                {getTranslation(language, 'body')}
              </button>
            </div>

            <div className="tab-content">
              {activeTab === 'headers' && (
                <HeadersTab 
                  headers={request.headers}
                  onChange={(headers) => updateRequest('headers', headers)}
                />
              )}
              {activeTab === 'params' && (
                <ParamsTab 
                  params={request.params}
                  onChange={(params) => updateRequest('params', params)}
                />
              )}
              {activeTab === 'body' && (
                <BodyTab 
                  body={request.body}
                  bodyType={request.bodyType}
                  onChange={(body, bodyType) => {
                    updateRequest('body', body);
                    updateRequest('bodyType', bodyType);
                  }}
                />
              )}
            </div>
          </div>
        </div>

        <div className="response-section">
          <ResponseViewer response={response} />
        </div>
      </div>
    </div>
  );
};

export default RequestBuilder;
