import React, { useState } from 'react';
import { Clock, FileText, AlertCircle, CheckCircle } from 'lucide-react';
import { useSettings } from '../context/SettingsContext';
import './ResponseViewer.css';

const ResponseViewer = ({ response }) => {
  const { language } = useSettings();
  const [activeTab, setActiveTab] = useState('body');

  const formatResponseData = (data) => {
    try {
      // 尝试解析为JSON
      const parsed = JSON.parse(data);
      return JSON.stringify(parsed, null, 2);
    } catch {
      // 如果不是JSON，直接返回原数据
      return data;
    }
  };

  const getStatusColor = (status) => {
    if (status >= 200 && status < 300) return '#28a745';
    if (status >= 300 && status < 400) return '#ffc107';
    if (status >= 400 && status < 500) return '#fd7e14';
    if (status >= 500) return '#dc3545';
    return '#6c757d';
  };

  const formatHeaders = (headers) => {
    return Object.entries(headers)
      .map(([key, value]) => `${key}: ${value}`)
      .join('\n');
  };

  return (
    <div className="response-viewer">
      {/* 去掉顶部 response-header 区域 */}

      {/* 无论是否有response都显示标签页 */}
      <div className="response-tabs">
        <div className="tab-container">
          <button 
            className={`tab active`}
            style={{cursor: 'default'}}
          >
            {language === 'zh' ? '响应' : 'Response'}
          </button>
          {response && !response.error && (
            <span style={{
              marginLeft: 12,
              fontSize: 14,
              color: getStatusColor(response.status),
              fontWeight: 500,
              display: 'inline-flex',
              alignItems: 'center',
              gap: 16
            }}>
              <span style={{color: getStatusColor(response.status), fontWeight: 600}}>{response.status}</span>
              <span style={{color: '#888'}}>{response.time}ms</span>
              {response.size && (
                <span style={{color: '#888'}}>{response.size} bytes</span>
              )}
            </span>
          )}
        </div>
        <div className="tab-content">
          <div className="response-body">
            {!response ? (
              null
            ) : response.error ? (
              <div className="error-message">
                <h4>{language === 'zh' ? '请求错误' : 'Request Error'}</h4>
                <p>{response.message}</p>
              </div>
            ) : (
              <>
                <pre className="headers-data" style={{marginBottom: 16, height: 80, maxHeight: 120, overflow: 'auto'}}>{formatHeaders(response.headers)}</pre>
                <pre className="response-data" style={{height: '220px', maxHeight: '320px', overflow: 'auto'}}>
                  {formatResponseData(response.data)}
                </pre>
              </>
            )}
          </div>
        </div>
      </div>
    </div>
  );
};

export default ResponseViewer;
