import React, { useState, useEffect } from 'react';
import { Plus, Folder, FileText, Trash2, Edit, Play } from 'lucide-react';
import './Collections.css';

const Collections = () => {
  const [collections, setCollections] = useState([]);
  const [selectedCollection, setSelectedCollection] = useState(null);
  const [showNewCollectionModal, setShowNewCollectionModal] = useState(false);
  const [newCollectionName, setNewCollectionName] = useState('');

  useEffect(() => {
    loadCollections();
  }, []);

  const loadCollections = () => {
    const savedCollections = JSON.parse(localStorage.getItem('apiCollections') || '[]');
    setCollections(savedCollections);
  };

  const saveCollections = (newCollections) => {
    localStorage.setItem('apiCollections', JSON.stringify(newCollections));
    setCollections(newCollections);
  };

  const createCollection = () => {
    if (!newCollectionName.trim()) {
      alert('请输入集合名称');
      return;
    }

    const newCollection = {
      id: Date.now(),
      name: newCollectionName,
      description: '',
      requests: [],
      createdAt: new Date().toISOString()
    };

    const newCollections = [...collections, newCollection];
    saveCollections(newCollections);
    setNewCollectionName('');
    setShowNewCollectionModal(false);
  };

  const deleteCollection = (id) => {
    if (window.confirm('确定要删除这个集合吗？')) {
      const newCollections = collections.filter(c => c.id !== id);
      saveCollections(newCollections);
      if (selectedCollection && selectedCollection.id === id) {
        setSelectedCollection(null);
      }
    }
  };

  const addRequestToCollection = (collectionId, request) => {
    const newCollections = collections.map(c => {
      if (c.id === collectionId) {
        return {
          ...c,
          requests: [...c.requests, {
            id: Date.now(),
            ...request,
            addedAt: new Date().toISOString()
          }]
        };
      }
      return c;
    });
    saveCollections(newCollections);
  };

  const removeRequestFromCollection = (collectionId, requestId) => {
    const newCollections = collections.map(c => {
      if (c.id === collectionId) {
        return {
          ...c,
          requests: c.requests.filter(r => r.id !== requestId)
        };
      }
      return c;
    });
    saveCollections(newCollections);
  };

  const getStatusColor = (status) => {
    if (status >= 200 && status < 300) return '#28a745';
    if (status >= 300 && status < 400) return '#ffc107';
    if (status >= 400 && status < 500) return '#fd7e14';
    if (status >= 500) return '#dc3545';
    return '#6c757d';
  };

  return (
    <div className="collections-page">
      <div className="collections-header">
        <h2>集合管理</h2>
        <button 
          className="btn btn-primary"
          onClick={() => setShowNewCollectionModal(true)}
        >
          <Plus size={16} />
          新建集合
        </button>
      </div>

      <div className="collections-content">
        <div className="collections-list">
          {collections.length === 0 ? (
            <div className="empty-collections">
              <Folder size={48} />
              <p>暂无集合，点击上方按钮创建</p>
            </div>
          ) : (
            collections.map((collection) => (
              <div 
                key={collection.id}
                className={`collection-item ${selectedCollection?.id === collection.id ? 'selected' : ''}`}
                onClick={() => setSelectedCollection(collection)}
              >
                <div className="collection-header">
                  <Folder size={20} />
                  <div className="collection-info">
                    <h4>{collection.name}</h4>
                    <p>{collection.requests.length} 个请求</p>
                  </div>
                  <button
                    className="btn btn-danger remove-btn"
                    onClick={(e) => {
                      e.stopPropagation();
                      deleteCollection(collection.id);
                    }}
                  >
                    <Trash2 size={14} />
                  </button>
                </div>
              </div>
            ))
          )}
        </div>

        {selectedCollection && (
          <div className="collection-detail">
            <div className="detail-header">
              <h3>{selectedCollection.name}</h3>
              <div className="detail-actions">
                <button className="btn btn-primary">
                  <Edit size={16} />
                  编辑
                </button>
              </div>
            </div>

            <div className="requests-list">
              {selectedCollection.requests.length === 0 ? (
                <div className="empty-requests">
                  <FileText size={32} />
                  <p>此集合暂无请求</p>
                </div>
              ) : (
                selectedCollection.requests.map((request) => (
                  <div key={request.id} className="request-item">
                    <div className="request-info">
                      <div 
                        className="method-badge"
                        style={{ backgroundColor: getStatusColor(request.response?.status || 0) }}
                      >
                        {request.method}
                      </div>
                      <div className="request-url">{request.url}</div>
                    </div>
                    <div className="request-actions">
                      <button className="btn btn-primary">
                        <Play size={14} />
                        执行
                      </button>
                      <button 
                        className="btn btn-danger"
                        onClick={() => removeRequestFromCollection(selectedCollection.id, request.id)}
                      >
                        <Trash2 size={14} />
                      </button>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>
        )}
      </div>

      {/* 新建集合模态框 */}
      {showNewCollectionModal && (
        <div className="modal-overlay">
          <div className="modal">
            <div className="modal-header">
              <h3>新建集合</h3>
              <button 
                className="modal-close"
                onClick={() => setShowNewCollectionModal(false)}
              >
                ×
              </button>
            </div>
            <div className="modal-body">
              <div className="form-group">
                <label>集合名称</label>
                <input
                  type="text"
                  className="input"
                  placeholder="输入集合名称"
                  value={newCollectionName}
                  onChange={(e) => setNewCollectionName(e.target.value)}
                  onKeyPress={(e) => e.key === 'Enter' && createCollection()}
                />
              </div>
            </div>
            <div className="modal-footer">
              <button 
                className="btn btn-primary"
                onClick={createCollection}
              >
                创建
              </button>
              <button 
                className="btn"
                onClick={() => setShowNewCollectionModal(false)}
              >
                取消
              </button>
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default Collections;
