import React, { useState, useEffect, useCallback } from 'react';
import { Settings, Plus, Trash2 } from 'lucide-react';
import './EnvironmentConfig.css';

const EnvironmentConfig = ({ onHostChange }) => {
  console.log('EnvironmentConfig component rendered'); // 调试日志

  const [environments, setEnvironments] = useState([]);
  const [currentEnvironment, setCurrentEnvironment] = useState('');
  const [showConfig, setShowConfig] = useState(false);
  const [newEnvName, setNewEnvName] = useState('');
  const [newEnvHost, setNewEnvHost] = useState('');

  const loadEnvironments = useCallback(() => {
    const savedEnvironments = JSON.parse(localStorage.getItem('apiEnvironments') || '[]');
    const currentEnv = localStorage.getItem('currentEnvironment') || '';

    setEnvironments(savedEnvironments);
    setCurrentEnvironment(currentEnv);

    // 如果有当前环境，通知父组件host变化
    if (currentEnv) {
      const env = savedEnvironments.find(e => e.name === currentEnv);
      if (env && onHostChange) {
        onHostChange(env.host);
      }
    }
  }, [onHostChange]);

  useEffect(() => {
    loadEnvironments();
  }, [loadEnvironments]);

  const saveEnvironments = (newEnvironments) => {
    localStorage.setItem('apiEnvironments', JSON.stringify(newEnvironments));
    setEnvironments(newEnvironments);
  };

  const addEnvironment = () => {
    if (!newEnvName.trim() || !newEnvHost.trim()) {
      alert('请输入环境名称和host');
      return;
    }

    const newEnvironment = {
      name: newEnvName,
      host: newEnvHost,
      variables: []
    };

    const newEnvironments = [...environments, newEnvironment];
    saveEnvironments(newEnvironments);

    setNewEnvName('');
    setNewEnvHost('');
    setShowConfig(false);
  };

  const deleteEnvironment = (name) => {
    if (window.confirm(`确定要删除环境 "${name}" 吗？`)) {
      const newEnvironments = environments.filter(e => e.name !== name);
      saveEnvironments(newEnvironments);

      if (currentEnvironment === name) {
        setCurrentEnvironment('');
        localStorage.removeItem('currentEnvironment');
        if (onHostChange) {
          onHostChange('');
        }
      }
    }
  };

  const selectEnvironment = (name) => {
    setCurrentEnvironment(name);
    localStorage.setItem('currentEnvironment', name);

    const env = environments.find(e => e.name === name);
    if (env && onHostChange) {
      onHostChange(env.host);
    }
  };

  const getCurrentHost = () => {
    const env = environments.find(e => e.name === currentEnvironment);
    return env ? env.host : '';
  };

  return (
    <div className="environment-config">
      <div className="config-header">
        <div className="current-env">
          <Settings size={16} />
          <span>当前环境: </span>
          <select 
            value={currentEnvironment} 
            onChange={(e) => selectEnvironment(e.target.value)}
            className="env-selector"
          >
            <option value="">选择环境</option>
            {environments.map(env => (
              <option key={env.name} value={env.name}>
                {env.name} ({env.host})
              </option>
            ))}
          </select>
        </div>
        <button 
          className="btn btn-primary"
          onClick={() => setShowConfig(!showConfig)}
        >
          <Settings size={16} />
          环境配置
        </button>
      </div>

      {currentEnvironment && (
        <div className="current-host">
          <strong>基础URL:</strong> {getCurrentHost()}
        </div>
      )}

      {showConfig && (
        <div className="config-panel">
          <div className="config-section">
            <h4>添加新环境</h4>
            <div className="env-form">
              <input
                type="text"
                className="input"
                placeholder="环境名称 (例如: 开发环境)"
                value={newEnvName}
                onChange={(e) => setNewEnvName(e.target.value)}
              />
              <input
                type="text"
                className="input"
                placeholder="基础URL (例如: https://api.example.com)"
                value={newEnvHost}
                onChange={(e) => setNewEnvHost(e.target.value)}
              />
              <button className="btn btn-success" onClick={addEnvironment}>
                <Plus size={16} />
                添加
              </button>
            </div>
          </div>

          <div className="config-section">
            <h4>环境列表</h4>
            <div className="env-list">
              {environments.length === 0 ? (
                <p className="empty-env">暂无环境配置</p>
              ) : (
                environments.map(env => (
                  <div key={env.name} className="env-item">
                    <div className="env-info">
                      <strong>{env.name}</strong>
                      <span>{env.host}</span>
                    </div>
                    <div className="env-actions">
                      <button
                        className="btn btn-primary"
                        onClick={() => selectEnvironment(env.name)}
                        disabled={currentEnvironment === env.name}
                      >
                        选择
                      </button>
                      <button
                        className="btn btn-danger"
                        onClick={() => deleteEnvironment(env.name)}
                      >
                        <Trash2 size={14} />
                      </button>
                    </div>
                  </div>
                ))
              )}
            </div>
          </div>

          <div className="config-section">
            <h4>预设环境</h4>
            <div className="preset-envs">
              {[
                { name: '本地开发', host: 'http://localhost:3000' },
                { name: '测试环境', host: 'https://api-test.example.com' },
                { name: '生产环境', host: 'https://api.example.com' },
                { name: 'Mock服务', host: 'https://mockapi.io/project/123' }
              ].map((preset, index) => (
                <button
                  key={index}
                  className="btn preset-btn"
                  onClick={() => {
                    setNewEnvName(preset.name);
                    setNewEnvHost(preset.host);
                  }}
                >
                  {preset.name}
                </button>
              ))}
            </div>
          </div>
        </div>
      )}
    </div>
  );
};

export default EnvironmentConfig;
