import React, { useState } from 'react';
import { Plus, Trash2 } from 'lucide-react';
import { useSettings } from '../context/SettingsContext';
import './HeadersTab.css';

const HeadersTab = ({ headers, onChange }) => {
  const { language } = useSettings();
  const [newKey, setNewKey] = useState('');

  const addHeader = () => {
    if (newKey.trim() === '') return;
    onChange([...headers, { key: newKey, value: '', enabled: true }]);
    setNewKey('');
  };

  const updateHeader = (index, field, value) => {
    const newHeaders = [...headers];
    newHeaders[index] = { ...newHeaders[index], [field]: value };
    onChange(newHeaders);
  };

  const removeHeader = (index) => {
    const newHeaders = headers.filter((_, i) => i !== index);
    onChange(newHeaders);
  };

  const toggleHeader = (index) => {
    const newHeaders = [...headers];
    newHeaders[index] = { ...newHeaders[index], enabled: !newHeaders[index].enabled };
    onChange(newHeaders);
  };

  return (
    <div className="headers-tab">
      <div className="headers-header-capsule">
        <input
          className="header-capsule-input"
          type="text"
          placeholder={language === 'zh' ? '添加请求头名称' : 'Add header name'}
          value={newKey}
          onChange={e => setNewKey(e.target.value)}
          onKeyDown={e => { if (e.key === 'Enter') addHeader(); }}
        />
        <button className="header-capsule-btn" onClick={addHeader} title={language === 'zh' ? '添加' : 'Add'}>
          <Plus size={22} />
        </button>
      </div>

      <div className="headers-list">
        {headers.length === 0 ? (
          <div className="empty-state">
            <p>{language === 'zh' ? '暂无请求头，使用上方+添加' : 'No headers, use + above to add'}</p>
          </div>
        ) : (
          headers.map((header, index) => (
            <div key={index} className="header-row">
              <div className="header-controls">
                <input
                  type="checkbox"
                  checked={header.enabled}
                  onChange={() => toggleHeader(index)}
                  className="header-checkbox"
                />
                <button
                  className="btn btn-danger remove-btn"
                  onClick={() => removeHeader(index)}
                >
                  <Trash2 size={14} />
                </button>
              </div>
              <input
                type="text"
                className="header-key"
                placeholder={language === 'zh' ? '请求头名称 (例如: Content-Type)' : 'Header name (e.g., Content-Type)'}
                value={header.key}
                onChange={(e) => updateHeader(index, 'key', e.target.value)}
                disabled={!header.enabled}
              />
              <input
                type="text"
                className="header-value"
                placeholder={language === 'zh' ? '请求头值 (例如: application/json)' : 'Header value (e.g., application/json)'}
                value={header.value}
                onChange={(e) => updateHeader(index, 'value', e.target.value)}
                disabled={!header.enabled}
              />
            </div>
          ))
        )}
      </div>

      <div className="preset-headers">
        <h4>{language === 'zh' ? '常用请求头' : 'Common Headers'}</h4>
        <div className="preset-buttons">
          {[
            { key: 'Content-Type', value: 'application/json' },
            { key: 'Authorization', value: 'Bearer your-token' },
            { key: 'Accept', value: 'application/json' },
            { key: 'User-Agent', value: 'API-Testing-Platform/1.0' }
          ].map((preset, index) => (
            <button
              key={index}
              className="btn preset-btn"
              onClick={() => {
                const newHeaders = [...headers, { ...preset, enabled: true }];
                onChange(newHeaders);
              }}
            >
              {preset.key}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default HeadersTab;
