import React, { useState } from 'react';
import { useSettings } from '../context/SettingsContext';
import './Settings.css';

const Settings = () => {
  const { language, setLanguage, theme, setTheme, autoSave, setAutoSave } = useSettings();

  const languages = [
    { code: 'zh', name: '中文', flag: '🇨🇳' },
    { code: 'en', name: 'English', flag: '🇺🇸' }
  ];

  const themes = [
    { id: 'light', name: '浅色主题', icon: '☀️', description: '明亮清晰的界面' },
    { id: 'dark', name: '深色主题', icon: '🌙', description: '护眼的深色界面' }
  ];

  const handleLanguageChange = (langCode) => {
    setLanguage(langCode);
  };

  const handleThemeChange = (themeId) => {
    setTheme(themeId);
  };

  const handleAutoSaveChange = (checked) => {
    setAutoSave(checked);
  };

  return (
    <div className="settings-page">
      <div className="settings-header">
        <h2>设置</h2>
        <p>自定义你的API测试平台体验</p>
      </div>

      <div className="settings-content">
        {/* 语言设置 */}
        <div className="settings-section">
          <div className="section-header">
            <h3>语言设置</h3>
            <p>选择你偏好的界面语言</p>
          </div>

          <div className="language-options">
            {languages.map((lang) => (
              <div
                key={lang.code}
                className={`language-option ${language === lang.code ? 'active' : ''}`}
                onClick={() => handleLanguageChange(lang.code)}
              >
                <div className="language-flag">{lang.flag}</div>
                <div className="language-info">
                  <div className="language-name">{lang.name}</div>
                  <div className="language-code">{lang.code.toUpperCase()}</div>
                </div>
                {language === lang.code && (
                  <div className="language-check">✓</div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* 主题设置 */}
        <div className="settings-section">
          <div className="section-header">
            <h3>主题设置</h3>
            <p>选择你喜欢的界面主题</p>
          </div>

          <div className="theme-options">
            {themes.map((themeOption) => (
              <div
                key={themeOption.id}
                className={`theme-option ${theme === themeOption.id ? 'active' : ''}`}
                onClick={() => handleThemeChange(themeOption.id)}
              >
                <div className="theme-icon">{themeOption.icon}</div>
                <div className="theme-info">
                  <div className="theme-name">{themeOption.name}</div>
                  <div className="theme-description">{themeOption.description}</div>
                </div>
                {theme === themeOption.id && (
                  <div className="theme-check">✓</div>
                )}
              </div>
            ))}
          </div>
        </div>

        {/* 其他设置 */}
        <div className="settings-section">
          <div className="section-header">
            <h3>其他设置</h3>
            <p>更多个性化选项</p>
          </div>

          <div className="other-settings">
            <div className="setting-item">
              <div className="setting-info">
                <div className="setting-name">自动保存</div>
                <div className="setting-description">自动保存请求历史</div>
              </div>
              <label className="switch">
                <input type="checkbox" defaultChecked />
                <span className="slider"></span>
              </label>
            </div>
          </div>
        </div>

        {/* 操作按钮 */}
        <div className="settings-actions">
          <button className="btn btn-secondary">重置设置</button>
          <button className="btn btn-primary">保存设置</button>
        </div>
      </div>
    </div>
  );
};

export default Settings;
