import React from 'react';
import { Send, Clock, FileText } from 'lucide-react';
import './RequestInfo.css';

const RequestInfo = ({ request, response, loading, error }) => {
  const formatHeaders = (headers) => {
    if (!headers || headers.length === 0) return '无';
    return headers
      .filter(header => header.key && header.value)
      .map(header => `${header.key}: ${header.value}`)
      .join('\n');
  };

  const formatParams = (params) => {
    if (!params || params.length === 0) return '无';
    return params
      .filter(param => param.key && param.value)
      .map(param => `${param.key}=${param.value}`)
      .join('\n');
  };

  const getMethodColor = (method) => {
    const colors = {
      GET: '#28a745',
      POST: '#007bff',
      PUT: '#ffc107',
      DELETE: '#dc3545',
      PATCH: '#fd7e14',
      HEAD: '#6c757d',
      OPTIONS: '#6c757d'
    };
    return colors[method] || '#6c757d';
  };

  return (
    <div className="request-info">
      <div className="info-header">
        <h3>请求信息</h3>
        <div className="request-status">
          {loading ? (
            <div className="status-loading">
              <Clock size={16} />
              <span>发送中...</span>
            </div>
          ) : response ? (
            <div className="status-completed">
              <Send size={16} />
              <span>请求完成</span>
            </div>
          ) : (
            <div className="status-ready">
              <FileText size={16} />
              <span>准备发送</span>
            </div>
          )}
        </div>
      </div>

      <div className="info-content">
        <div className="info-section">
          <h4>基本信息</h4>
          <div className="info-item">
            <strong>方法:</strong>
            <span 
              className="method-badge"
              style={{ backgroundColor: getMethodColor(request.method) }}
            >
              {request.method}
            </span>
          </div>
          <div className="info-item">
            <strong>URL:</strong>
            <span className="url-text">{request.url || '未设置'}</span>
          </div>
        </div>

        {request.headers && request.headers.length > 0 && (
          <div className="info-section">
            <h4>请求头</h4>
            <pre className="info-data">{formatHeaders(request.headers)}</pre>
          </div>
        )}

        {request.params && request.params.length > 0 && (
          <div className="info-section">
            <h4>URL参数</h4>
            <pre className="info-data">{formatParams(request.params)}</pre>
          </div>
        )}

        {request.body && (
          <div className="info-section">
            <h4>请求体</h4>
            <pre className="info-data">{request.body}</pre>
          </div>
        )}

        {response && (
          <div className="info-section">
            <h4>响应信息</h4>
            <div className="info-item">
              <strong>状态码:</strong>
              <span className={`status-code ${response.status >= 200 && response.status < 300 ? 'success' : 'error'}`}>
                {response.status} {response.statusText}
              </span>
            </div>
            <div className="info-item">
              <strong>响应时间:</strong>
              <span>{response.time}ms</span>
            </div>
            {response.size && (
              <div className="info-item">
                <strong>响应大小:</strong>
                <span>{response.size} bytes</span>
              </div>
            )}
          </div>
        )}
      </div>
    </div>
  );
};

export default RequestInfo;
