import React from 'react';
import { Plus, Trash2 } from 'lucide-react';
import { useSettings } from '../context/SettingsContext';
import './HeadersTab.css';

const HeadersTab = ({ headers, onChange, onAddHeader }) => {
  const { language } = useSettings();

  const updateHeader = (index, field, value) => {
    const newHeaders = [...headers];
    newHeaders[index] = { ...newHeaders[index], [field]: value };
    onChange(newHeaders);
  };

  const removeHeader = (index) => {
    const newHeaders = headers.filter((_, i) => i !== index);
    onChange(newHeaders);
  };

  const toggleHeader = (index) => {
    const newHeaders = [...headers];
    newHeaders[index] = { ...newHeaders[index], enabled: !newHeaders[index].enabled };
    onChange(newHeaders);
  };

  const addPresetHeader = (preset) => {
    onChange([...headers, { ...preset, enabled: true }]);
  };

  // 初始化时直接渲染5行常见请求头，全部勾选
  const rowCount = 8;
  const defaultHeaders = [
    { key: 'Content-Type', value: 'application/json', enabled: true },
    { key: 'Accept', value: '*/*', enabled: true },
    { key: 'User-Agent', value: 'PostmanRuntime/7.36.3', enabled: true },
    { key: 'Accept-Encoding', value: 'gzip, deflate, br', enabled: true },
    { key: 'Connection', value: 'keep-alive', enabled: true },
    { key: 'Origin', value: 'https://mock.postman.com', enabled: true },
    { key: 'Host', value: '<calculated when request is sent>', enabled: true },
    { key: 'Content-Length', value: '<calculated when request is sent>', enabled: true }
  ];
  let displayHeaders;
  if (headers.length === 0) {
    displayHeaders = defaultHeaders.slice(0, rowCount);
  } else if (headers.length < rowCount) {
    displayHeaders = [
      ...headers,
      ...defaultHeaders.slice(headers.length, rowCount)
    ];
  } else {
    displayHeaders = headers;
  }

  return (
    <div className="headers-tab">
      <div className="headers-list">
        {/* 表头行 */}
        <div className="header-row header-row-header">
          <div className="header-controls" style={{visibility: 'hidden'}}>
            <input type="checkbox" disabled className="header-checkbox" />
            <button className="btn remove-btn" disabled style={{opacity:0}}></button>
          </div>
          <div className="header-key header-th">Key</div>
          <div className="header-value header-th">Value</div>
        </div>
        {displayHeaders.map((header, index) => (
          <div key={index} className="header-row">
            <div className="header-controls">
              <input
                type="checkbox"
                checked={header.enabled}
                onChange={() => toggleHeader(index)}
                className="header-checkbox"
              />
              <button
                className="btn btn-danger remove-btn"
                onClick={() => removeHeader(index)}
              >
                <Trash2 size={14} />
              </button>
            </div>
            <input
              type="text"
              className="header-key"
              placeholder={language === 'zh' ? '请求头名称 (例如: Content-Type)' : 'Header name (e.g., Content-Type)'}
              value={header.key}
              onChange={(e) => updateHeader(index, 'key', e.target.value)}
              disabled={!header.enabled}
            />
            <input
              type="text"
              className="header-value"
              placeholder={language === 'zh' ? '请求头值 (例如: application/json)' : 'Header value (e.g., application/json)'}
              value={header.value}
              onChange={(e) => updateHeader(index, 'value', e.target.value)}
              disabled={!header.enabled}
            />
          </div>
        ))}
      </div>

      {/* 移除常用请求头 preset-headers 模块 */}
    </div>
  );
};

export default HeadersTab;
