import React, { useState } from 'react';
import { Clock, FileText, AlertCircle, CheckCircle } from 'lucide-react';
import { useSettings } from '../context/SettingsContext';
import { getTranslation } from '../locales/translations';
import './ResponseViewer.css';

const ResponseViewer = ({ response }) => {
  const { language } = useSettings();
  const [activeTab, setActiveTab] = useState('body');

  if (!response) {
    return (
      <div className="response-viewer">
        <div className="response-placeholder">
          <FileText size={48} />
          <p>{language === 'zh' ? '发送请求后，响应将显示在这里' : 'Response will be displayed here after sending request'}</p>
        </div>
      </div>
    );
  }

  const formatResponseData = (data) => {
    try {
      // 尝试解析为JSON
      const parsed = JSON.parse(data);
      return JSON.stringify(parsed, null, 2);
    } catch {
      // 如果不是JSON，直接返回原数据
      return data;
    }
  };

  const getStatusColor = (status) => {
    if (status >= 200 && status < 300) return '#28a745';
    if (status >= 300 && status < 400) return '#ffc107';
    if (status >= 400 && status < 500) return '#fd7e14';
    if (status >= 500) return '#dc3545';
    return '#6c757d';
  };

  const formatHeaders = (headers) => {
    return Object.entries(headers)
      .map(([key, value]) => `${key}: ${value}`)
      .join('\n');
  };

  return (
    <div className="response-viewer">
      <div className="response-header">
        <div className="response-status">
          {response.error ? (
            <div className="status-error">
              <AlertCircle size={16} />
              <span>{language === 'zh' ? '请求失败' : 'Request Failed'}</span>
            </div>
          ) : (
            <div 
              className="status-success"
              style={{ color: getStatusColor(response.status) }}
            >
              <CheckCircle size={16} />
              <span>{response.status} {response.statusText}</span>
            </div>
          )}
        </div>

        <div className="response-meta">
          <div className="meta-item">
            <Clock size={14} />
            <span>{response.time}ms</span>
          </div>
          {response.size && (
            <div className="meta-item">
              <FileText size={14} />
              <span>{response.size} bytes</span>
            </div>
          )}
        </div>
      </div>

      <div className="response-tabs">
        <div className="tab-container">
          <button 
            className={`tab ${activeTab === 'body' ? 'active' : ''}`}
            onClick={() => setActiveTab('body')}
          >
            {language === 'zh' ? '响应体' : 'Response Body'}
          </button>
          <button 
            className={`tab ${activeTab === 'headers' ? 'active' : ''}`}
            onClick={() => setActiveTab('headers')}
          >
            {language === 'zh' ? '响应头' : 'Response Headers'}
          </button>
        </div>

        <div className="tab-content">
          {activeTab === 'body' && (
            <div className="response-body">
              {response.error ? (
                <div className="error-message">
                  <h4>{language === 'zh' ? '请求错误' : 'Request Error'}</h4>
                  <p>{response.message}</p>
                </div>
              ) : (
                <pre className="response-data">
                  {formatResponseData(response.data)}
                </pre>
              )}
            </div>
          )}

          {activeTab === 'headers' && (
            <div className="response-headers">
              <pre className="headers-data">
                {formatHeaders(response.headers)}
              </pre>
            </div>
          )}
        </div>
      </div>
    </div>
  );
};

export default ResponseViewer;
