import React from 'react';
import { Plus, Trash2 } from 'lucide-react';
import { useSettings } from '../context/SettingsContext';
import './ParamsTab.css';

const ParamsTab = ({ params, onChange }) => {
  const { language } = useSettings();

  const addParam = () => {
    onChange([...params, { key: '', value: '', enabled: true }]);
  };

  const updateParam = (index, field, value) => {
    const newParams = [...params];
    newParams[index] = { ...newParams[index], [field]: value };
    onChange(newParams);
  };

  const removeParam = (index) => {
    const newParams = params.filter((_, i) => i !== index);
    onChange(newParams);
  };

  const toggleParam = (index) => {
    const newParams = [...params];
    newParams[index] = { ...newParams[index], enabled: !newParams[index].enabled };
    onChange(newParams);
  };

  return (
    <div className="params-tab">
      <div className="params-header">
        <h3>{language === 'zh' ? 'URL参数' : 'URL Parameters'}</h3>
        <button className="btn btn-primary" onClick={addParam}>
          <Plus size={16} />
          {language === 'zh' ? '添加参数' : 'Add Parameter'}
        </button>
      </div>

      <div className="params-list">
        {params.length === 0 ? (
          <div className="empty-state">
            <p>{language === 'zh' ? '暂无URL参数，点击上方按钮添加' : 'No URL parameters, click the button above to add'}</p>
          </div>
        ) : (
          params.map((param, index) => (
            <div key={index} className="param-row">
              <div className="param-controls">
                <input
                  type="checkbox"
                  checked={param.enabled}
                  onChange={() => toggleParam(index)}
                  className="param-checkbox"
                />
                <button
                  className="btn btn-danger remove-btn"
                  onClick={() => removeParam(index)}
                >
                  <Trash2 size={14} />
                </button>
              </div>
              <input
                type="text"
                className="param-key"
                placeholder={language === 'zh' ? '参数名 (例如: page)' : 'Parameter name (e.g., page)'}
                value={param.key}
                onChange={(e) => updateParam(index, 'key', e.target.value)}
                disabled={!param.enabled}
              />
              <input
                type="text"
                className="param-value"
                placeholder={language === 'zh' ? '参数值 (例如: 1)' : 'Parameter value (e.g., 1)'}
                value={param.value}
                onChange={(e) => updateParam(index, 'value', e.target.value)}
                disabled={!param.enabled}
              />
            </div>
          ))
        )}
      </div>

      <div className="preset-params">
        <h4>{language === 'zh' ? '常用参数' : 'Common Parameters'}</h4>
        <div className="preset-buttons">
          {[
            { key: 'page', value: '1' },
            { key: 'limit', value: '10' },
            { key: 'sort', value: 'created_at' },
            { key: 'order', value: 'desc' },
            { key: 'search', value: '' },
            { key: 'filter', value: 'active' }
          ].map((preset, index) => (
            <button
              key={index}
              className="btn preset-btn"
              onClick={() => {
                const newParams = [...params, { ...preset, enabled: true }];
                onChange(newParams);
              }}
            >
              {preset.key}
            </button>
          ))}
        </div>
      </div>
    </div>
  );
};

export default ParamsTab;
