import React from 'react';
import { Link, useLocation } from 'react-router-dom';
import { Send, History, FolderOpen, Settings } from 'lucide-react';
import { useSettings } from '../context/SettingsContext';
import { getTranslation } from '../locales/translations';
import './Header.css';

const Header = () => {
  const location = useLocation();
  const { language } = useSettings();

  return (
    <header className="header">
      <div className="header-content">
        <div className="logo">
          <Send className="logo-icon" />
          <h1>{getTranslation(language, 'appTitle')}</h1>
        </div>

        <nav className="nav">
          <Link 
            to="/" 
            className={`nav-link ${location.pathname === '/' ? 'active' : ''}`}
          >
            <Send size={18} />
            {getTranslation(language, 'requestBuilder')}
          </Link>
          <Link 
            to="/history" 
            className={`nav-link ${location.pathname === '/history' ? 'active' : ''}`}
          >
            <History size={18} />
            {getTranslation(language, 'history')}
          </Link>
          <Link 
            to="/collections" 
            className={`nav-link ${location.pathname === '/collections' ? 'active' : ''}`}
          >
            <FolderOpen size={18} />
            {getTranslation(language, 'collections')}
          </Link>
          <Link 
            to="/settings" 
            className={`nav-link ${location.pathname === '/settings' ? 'active' : ''}`}
          >
            <Settings size={18} />
            {getTranslation(language, 'settings')}
          </Link>
        </nav>
      </div>
    </header>
  );
};

export default Header;
