import React from 'react';
import './BodyTab.css';

const BodyTab = ({ body, bodyType, onChange }) => {
  const handleBodyChange = (newBody) => {
    onChange(newBody, bodyType);
  };

  const handleTypeChange = (newType) => {
    onChange(body, newType);
  };

  const getPlaceholder = () => {
    switch (bodyType) {
      case 'json':
        return `{
  "name": "John Doe",
  "email": "john@example.com",
  "age": 30
}`;
      case 'form':
        return `name=John Doe&email=john@example.com&age=30`;
      case 'xml':
        return `<user>
  <name>John Doe</name>
  <email>john@example.com</email>
  <age>30</age>
</user>`;
      default:
        return '输入请求体内容...';
    }
  };

  return (
    <div className="body-tab">
      <div className="body-header">
        <h3>请求体</h3>
        <div className="body-type-selector">
          <label>类型:</label>
          <select 
            value={bodyType} 
            onChange={(e) => handleTypeChange(e.target.value)}
            className="body-type-select"
          >
            <option value="json">JSON</option>
            <option value="form">Form Data</option>
            <option value="xml">XML</option>
            <option value="text">Text</option>
          </select>
        </div>
      </div>

      <div className="body-content">
        <textarea
          className="body-textarea"
          placeholder={getPlaceholder()}
          value={body}
          onChange={(e) => handleBodyChange(e.target.value)}
          rows={15}
        />
      </div>

      <div className="body-templates">
        <h4>请求体模板</h4>
        <div className="template-buttons">
          {bodyType === 'json' && (
            <>
              <button
                className="btn template-btn"
                onClick={() => handleBodyChange(`{
  "name": "John Doe",
  "email": "john@example.com",
  "age": 30,
  "active": true
}`)}
              >
                用户数据
              </button>
              <button
                className="btn template-btn"
                onClick={() => handleBodyChange(`{
  "title": "New Post",
  "content": "This is the content of the post",
  "tags": ["tech", "programming"],
  "published": false
}`)}
              >
                文章数据
              </button>
            </>
          )}
          {bodyType === 'form' && (
            <>
              <button
                className="btn template-btn"
                onClick={() => handleBodyChange('name=John Doe&email=john@example.com&age=30')}
              >
                用户表单
              </button>
              <button
                className="btn template-btn"
                onClick={() => handleBodyChange('title=New Post&content=Post content&published=false')}
              >
                文章表单
              </button>
            </>
          )}
          {bodyType === 'xml' && (
            <>
              <button
                className="btn template-btn"
                onClick={() => handleBodyChange(`<user>
  <name>John Doe</name>
  <email>john@example.com</email>
  <age>30</age>
</user>`)}
              >
                用户XML
              </button>
            </>
          )}
        </div>
      </div>
    </div>
  );
};

export default BodyTab;
