import React, { useState, useEffect } from 'react';
import { useNavigate } from 'react-router-dom';
import { Clock, Trash2, RotateCcw, GripVertical } from 'lucide-react';
import './History.css';

const History = () => {
  const navigate = useNavigate();
  const [history, setHistory] = useState([]);
  const [selectedItem, setSelectedItem] = useState(null);
  const [isResizing, setIsResizing] = useState(false);
  const [listWidth, setListWidth] = useState(400); // 默认列表宽度
  const [draggedItem, setDraggedItem] = useState(null);
  const [dragOverItem, setDragOverItem] = useState(null);

  useEffect(() => {
    loadHistory();
  }, []);

  const loadHistory = () => {
    const savedHistory = JSON.parse(localStorage.getItem('apiHistory') || '[]');
    setHistory(savedHistory);
  };

  const clearHistory = () => {
    if (window.confirm('确定要清空所有历史记录吗？')) {
      localStorage.removeItem('apiHistory');
      setHistory([]);
      setSelectedItem(null);
    }
  };

  // const removeHistoryItem = (id) => {
  //   const newHistory = history.filter(item => item.id !== id);
  //   localStorage.setItem('apiHistory', JSON.stringify(newHistory));
  //   setHistory(newHistory);
  //   if (selectedItem && selectedItem.id === id) {
  //     setSelectedItem(null);
  //   }
  // };



  const formatTimestamp = (timestamp) => {
    const date = new Date(timestamp);
    return date.toLocaleString('zh-CN');
  };

  const getStatusColor = (status) => {
    if (status >= 200 && status < 300) return '#28a745';
    if (status >= 300 && status < 400) return '#ffc107';
    if (status >= 400 && status < 500) return '#fd7e14';
    if (status >= 500) return '#dc3545';
    return '#6c757d';
  };

  const handleRetry = (historyItem) => {
    // 将历史记录数据存储到localStorage，供RequestBuilder使用
    const retryData = {
      method: historyItem.method,
      url: historyItem.url,
      headers: historyItem.headers || [],
      params: historyItem.params || [],
      body: historyItem.body || '',
      bodyType: historyItem.bodyType || 'json'
    };

    localStorage.setItem('retryRequestData', JSON.stringify(retryData));

    // 跳转到请求构建器页面
    navigate('/');
  };

  // 拉伸开始
  const handleResizeStart = (e) => {
    e.preventDefault();
    setIsResizing(true);
    document.addEventListener('mousemove', handleResizeMove);
    document.addEventListener('mouseup', handleResizeEnd);
  };

  // 拉伸移动
  const handleResizeMove = (e) => {
    if (!isResizing) return;

    const container = document.querySelector('.history-content');
    if (container) {
      const containerRect = container.getBoundingClientRect();
      const newWidth = e.clientX - containerRect.left;

      // 限制最小和最大宽度
      const minWidth = 200;
      const maxWidth = containerRect.width - 200;

      if (newWidth >= minWidth && newWidth <= maxWidth) {
        setListWidth(newWidth);
      }
    }
  };

  // 拉伸结束
  const handleResizeEnd = () => {
    setIsResizing(false);
    document.removeEventListener('mousemove', handleResizeMove);
    document.removeEventListener('mouseup', handleResizeEnd);
  };

  // 拖拽开始
  const handleDragStart = (e, item) => {
    console.log('Drag start:', item.id); // 调试日志
    setDraggedItem(item);
    e.dataTransfer.effectAllowed = 'move';
    e.dataTransfer.setData('text/html', item.id);
  };

  // 拖拽结束
  const handleDragEnd = () => {
    setDraggedItem(null);
    setDragOverItem(null);
  };

  // 拖拽悬停
  const handleDragOver = (e, item) => {
    e.preventDefault();
    if (draggedItem && draggedItem.id !== item.id) {
      setDragOverItem(item);
    }
  };

  // 拖拽进入
  const handleDragEnter = (e, item) => {
    e.preventDefault();
    if (draggedItem && draggedItem.id !== item.id) {
      setDragOverItem(item);
    }
  };

  // 拖拽离开
  const handleDragLeave = (e) => {
    e.preventDefault();
    setDragOverItem(null);
  };

  // 拖拽放置
  const handleDrop = (e, targetItem) => {
    e.preventDefault();
    console.log('Drop:', draggedItem?.id, '->', targetItem.id); // 调试日志
    if (!draggedItem || draggedItem.id === targetItem.id) {
      return;
    }

    const draggedIndex = history.findIndex(item => item.id === draggedItem.id);
    const targetIndex = history.findIndex(item => item.id === targetItem.id);

    if (draggedIndex !== -1 && targetIndex !== -1) {
      const newHistory = [...history];
      const [removed] = newHistory.splice(draggedIndex, 1);
      newHistory.splice(targetIndex, 0, removed);

      setHistory(newHistory);
      localStorage.setItem('apiHistory', JSON.stringify(newHistory));
      console.log('History reordered successfully'); // 调试日志
    }

    setDraggedItem(null);
    setDragOverItem(null);
  };

  return (
    <div className="history-page">
      <div className="history-header">
        <h2>请求历史</h2>
        <button className="btn btn-danger" onClick={clearHistory}>
          <Trash2 size={16} />
          清空历史
        </button>
      </div>

      <div className="history-content">
        <div 
          className="history-list"
          style={{ width: `${listWidth}px` }}
        >
          {history.length === 0 ? (
            <div className="empty-history">
              <Clock size={48} />
              <p>暂无历史记录</p>
            </div>
          ) : (
            history.map((item) => (
              <div 
                key={item.id} 
                className={`history-item ${selectedItem?.id === item.id ? 'selected' : ''} ${
                  draggedItem?.id === item.id ? 'dragging' : ''
                } ${
                  dragOverItem?.id === item.id ? 'drag-over' : ''
                }`}
                onClick={() => setSelectedItem(item)}
                draggable
                onDragStart={(e) => handleDragStart(e, item)}
                onDragEnd={handleDragEnd}
                onDragOver={(e) => handleDragOver(e, item)}
                onDragEnter={(e) => handleDragEnter(e, item)}
                onDragLeave={handleDragLeave}
                onDrop={(e) => handleDrop(e, item)}
              >
                <div 
                  className="drag-handle"
                  onMouseDown={(e) => e.stopPropagation()}
                >
                  <GripVertical size={16} />
                </div>
                <div className="history-item-content">
                  <div className="method-badge" style={{ 
                    backgroundColor: getStatusColor(item.response?.status || 0) 
                  }}>
                    {item.method}
                  </div>
                  <div className="history-url">{item.url}</div>
                  <div className="history-time">{formatTimestamp(item.timestamp)}</div>
                </div>
              </div>
            ))
          )}
        </div>

        <div 
          className="resize-handle"
          onMouseDown={handleResizeStart}
        />

        {selectedItem && (
          <div className="history-detail">
            <div className="detail-header">
              <h3>请求详情</h3>
              <button 
                className="btn btn-primary"
                onClick={() => handleRetry(selectedItem)}
              >
                <RotateCcw size={16} />
                重试
              </button>
            </div>

            <div className="detail-content">
              <div className="detail-section">
                <h4>请求信息</h4>
                <div className="detail-item">
                  <strong>方法:</strong> {selectedItem.method}
                </div>
                <div className="detail-item">
                  <strong>URL:</strong> {selectedItem.url}
                </div>
                <div className="detail-item">
                  <strong>时间:</strong> {formatTimestamp(selectedItem.timestamp)}
                </div>
              </div>

              {selectedItem.headers && selectedItem.headers.length > 0 && (
                <div className="detail-section">
                  <h4>请求头</h4>
                  {selectedItem.headers.map((header, index) => (
                    <div key={index} className="detail-item">
                      <strong>{header.key}:</strong> {header.value}
                    </div>
                  ))}
                </div>
              )}

              {selectedItem.params && selectedItem.params.length > 0 && (
                <div className="detail-section">
                  <h4>URL参数</h4>
                  {selectedItem.params.map((param, index) => (
                    <div key={index} className="detail-item">
                      <strong>{param.key}:</strong> {param.value}
                    </div>
                  ))}
                </div>
              )}

              {selectedItem.body && (
                <div className="detail-section">
                  <h4>请求体</h4>
                  <pre className="detail-body">{selectedItem.body}</pre>
                </div>
              )}

              {selectedItem.response && (
                <div className="detail-section">
                  <h4>响应信息</h4>
                  <div className="detail-item">
                    <strong>状态:</strong> {selectedItem.response.status} {selectedItem.response.statusText}
                  </div>
                  <div className="detail-item">
                    <strong>响应时间:</strong> {selectedItem.response.time}ms
                  </div>
                  {selectedItem.response.size && (
                    <div className="detail-item">
                      <strong>响应大小:</strong> {selectedItem.response.size} bytes
                    </div>
                  )}
                  {selectedItem.response.data && (
                    <div className="detail-item">
                      <strong>响应体:</strong>
                      <pre className="detail-response">{selectedItem.response.data}</pre>
                    </div>
                  )}
                </div>
              )}
            </div>
          </div>
        )}
      </div>
    </div>
  );
};

export default History;
